bool darkThemeChosen() {
  if (ain.read() > 0.0 && ain.read() < 0.5) {
    return false;
  } else {
    return true;
  }
}

Bitmap backgroundBM = {128, 32, 16, groundWSun};
// Display size: 128x32
Bitmap backgroundBMd = {128, 32, 16, groundWSunD};

void printBackgroundL(C12832 &_lcd) { _lcd.print_bm(backgroundBM, xLoc, yLoc); }

void printBackgroundD(C12832 &_lcd) {
  _lcd.print_bm(backgroundBMd, xLoc, yLoc);
}

void printBackG(C12832 &_lcd, bool &centPressed) {
  if (centPressed) {
    if (darkThemeChosen()) {
      printBackgroundD(_lcd);
    } else {
      printBackgroundL(_lcd);
    }
    //_lcd.copy_to_lcd();
  }
}

