void printStartSL(C12832 &_lcd) {
  _lcd.print_bm(boot1Lbm, xLoc2, yLoc2);
}

void printStartSD(C12832 &_lcd) {
  _lcd.print_bm(boot1Dbm, xLoc2, yLoc2);

}
void printStartS(C12832 &_lcd, bool &centPressed) {
  if (!centPressed) {
    if (darkThemeChosen2()) {
      printStartSD(_lcd);
    } else {
      printStartSL(_lcd);
    }
    //_lcd.copy_to_lcd();
  }
}
